package ua.app; import ua.exception.*; import ua.model.*; import ua.util.*; import java.io.*; import java.util.*; import java.util.logging.*; public class Main{ private static final Logger LOG=LogUtil.createLogger(Main.class); public static void main(String[]a){ try{var p=read("players.txt",true);var c=read("coaches.txt",false);LOG.info("Players="+p.size()+" Coaches="+c.size());}catch(Exception e){LOG.severe(e.getMessage());}} static <T> List<T> read(String f,boolean isP)throws Exception{ List<T>L=new ArrayList<>(); BufferedReader r=new BufferedReader(new FileReader(f)); String line; while((line=r.readLine())!=null){ try{ if(isP)L.add((T)Player.fromCsv(line)); else L.add((T)Coach.fromCsv(line)); }catch(Exception e){LOG.warning(e.getMessage());}} r.close(); return L;} }