package ua.model; import ua.exception.InvalidDataException; import ua.util.ValidationHelper; import ua.util.LogUtil; import java.util.logging.*; public class Coach{ private static final Logger LOG=LogUtil.createLogger(Coach.class); private final int id; private final String firstName; private final String lastName; private final String role; public Coach(int id,String f,String l,String r){ this.id=ValidationHelper.requirePositive(id,"id"); this.firstName=ValidationHelper.requireNonBlank(f,"firstName"); this.lastName=ValidationHelper.requireNonBlank(l,"lastName"); this.role=ValidationHelper.requireNonBlank(r,"role"); LOG.info("Coach created: "+this);} public static Coach fromCsv(String line)throws InvalidDataException{ if(line==null||line.trim().isEmpty()) throw new InvalidDataException("Empty line"); String[]p=line.split(";"); if(p.length!=4) throw new InvalidDataException("Bad format: "+line); try{ int id=Integer.parseInt(p[0].trim()); return new Coach(id,p[1],p[2],p[3]); }catch(Exception e){ throw new InvalidDataException("Parse error: "+line,e);} } public String toString(){return "Coach{"+id+","+firstName+","+lastName+","+role+"}";} }