package ua.model; import ua.exception.InvalidDataException; import ua.util.ValidationHelper; import ua.util.LogUtil; import java.util.*; import java.util.logging.*; public class Player{ private static final Logger LOG=LogUtil.createLogger(Player.class); private final int id; private final String firstName; private final String lastName; private final int number; public Player(int id,String f,String l,int n){ this.id=ValidationHelper.requirePositive(id,"id"); this.firstName=ValidationHelper.requireNonBlank(f,"firstName"); this.lastName=ValidationHelper.requireNonBlank(l,"lastName"); this.number=ValidationHelper.requireInRange(n,1,99,"number"); LOG.info("Player created: "+this);} public static Player fromCsv(String line)throws InvalidDataException{ if(line==null||line.trim().isEmpty()) throw new InvalidDataException("Empty line"); String[]p=line.split(";"); if(p.length!=4) throw new InvalidDataException("Bad format: "+line); try{ int id=Integer.parseInt(p[0].trim()); return new Player(id,p[1],p[2],Integer.parseInt(p[3].trim())); }catch(Exception e){ throw new InvalidDataException("Parse error: "+line,e);} } public String toString(){return "Player{"+id+","+firstName+","+lastName+","+number+"}";} }