package ua.util; public final class ValidationHelper{ private ValidationHelper(){} public static String requireNonBlank(String v,String f){ if(v==null||v.trim().isEmpty()) throw new IllegalArgumentException(f); return v.trim(); } public static int requirePositive(int v,String f){ if(v<=0) throw new IllegalArgumentException(f); return v; } public static int requireInRange(int v,int a,int b,String f){ if(v<a||v>b) throw new IllegalArgumentException(f); return v; }}